import { Coordinate, LineType, LineWidth, Time } from 'lightweight-charts';
export type TokenGraphData = {
    prices: {
        date: number;
        ethValue?: number;
        ccyValue?: number;
    }[];
};
export type SeriesConfig = {
    color: string;
    lineType: LineType;
    lineWidth: LineWidth;
    priceLineVisible: boolean;
    crosshairMarkerRadius: number;
    crosshairMarkerBackgroundColor: string;
    crosshairMarkerBorderColor: string;
    crosshairMarkerBorderWidth: number;
};
export type TimeFrameOption = "oneDay" | "oneWeek" | "oneMonth" | "oneYear" | "allTime";
export type ChartAsset = {
    unitCurrencyValue?: number | string;
};
export type CrosshairMoveProps = {
    x: Coordinate;
    y: Coordinate;
    time: Time;
    price: number;
    width: number;
    height: number;
} | undefined;
